mapper.select:
SELECT id, user_id, ip, location, operate_time
FROM tb_login_log
LIMIT #{param.start}, #{param.length}

SELECT COUNT(id) FROM tb_operate_log
opt_module = CONCAT('%', #{param.optType}, '%')
LIMIT #{param.start}, #{param.length}

SELECT id, user_id, ip, location, opt_module, opt_type, req_url, req_method, java_method, opt_req_msg, opt_resp_msg, opt_time FROM tb_operate_log
opt_module = CONCAT('%', #{param.optType}, '%')
LIMIT #{param.start}, #{param.length}

mapper.delete:
DELETE FROM tb_login_log
WHERE id IN

    #{id}

DELETE FROM tb_operate_log
WHERE id IN

    #{id}
